.pi_pop_up_text_5 {}

.pi_pop_up_expiration {
    padding: 0px 10px 0px 0px;
    color: #000;
    font-size: 12px;
    text-align: right;
    float: right;
    font-family: 'Arial Black', Arial, sans-serif;
    top: 140px;
}

.pi_pop_up_main_container {
    width: 100% !important;
    max-width: 500px !important;
    display: block !important;
    position: unset !important;
}

.pi_pop_up_main_container .pi_pop_up_content {
    overflow: hidden;
    display: inline-block;
    position: relative;
    min-height: 345px;
    width: 100%;
    max-width: 500px;
}

.pi_pop_up_main_container .pi_pop_up_content * {
    position: relative;
    z-index: 101
}

.pi_pop_up_main_container .pi_pop_up_content .pi_pop_up_bg {
    position: absolute;
    left: 0;
    width: 100%;
    z-index: 100;
    max-width: 500px;
    max-height: 350px;
    border-radius: 40px 40px 0px 0px;
}

.pi_pop_up_main_container .pi_pop_up_content .pi_pop_up_logo {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 30%;
    height: auto;
    max-width: 300px;
    max-height: 75px;
}

.pi_pop_up_main_container .pi_pop_up_form {
    position: relative;
    background-color: #fff;
    text-align: center;
    min-height: 200px;
    border-radius: 0px 0px 40px 40px;
    padding: 8px;
}

.pi_pop_up_main_container .pi_pop_up_form .pi_pop_up_input_fields input {
    display: block;
    margin: 7px;
    padding: 10px;
    max-width: 200px;
}

.pi_pop_up_main_container .pi_pop_up_form .pi_pop_up_helper_container .pi_pop_up_errors_text {
    color: white;
    padding: 2px;
    background-color: #f6787e;
    border: 1px solid #f01823;
    font-size: 11px;
    color: #f01823;
}

.pi_pop_up_main_container .pi_pop_up_form .pi_pop_up_helper_container .pi_pop_up_success_text {
    color: white;
    padding: 2px;
    background-color: #00af54;
    font-size: 11px;
}

.pi_pop_up_main_container .pi_pop_up_form button {}

.pi_pop_up_main_container .pi_pop_up_form button:hover {
    cursor: pointer;
}

.pi_pop_up_terms_and_conditions_close_button {
    font-size: 20px;
    /*line-height: 12px;
    padding: 10px;*/
    margin: 10px;
    float: right;
}

.pi_pop_up_main_container .pi_pop_up_form .pi_pop_up_terms_and_conditions_container {
    float: left;
    text-align: center;
    width: 50%;
    margin: auto;
    padding-top: 10px;
}

.pi_pop_up_main_container .pi_pop_up_form .pi_pop_up_close_button_container {
    float: right;
    text-align: center;
    width: 50%;
    margin: auto;
    padding-top: 10px;
}

.pi_pop_up_main_container .pi_pop_up_form .pi_pop_up_close_button {
    font-size: 16px;
    background: none;
    margin: 0;
    padding: 0;
    border: none;
    text-decoration: underline;
    color: #7b7b7b;
    font-family: Verdana, Geneva, sans-serif;
}

.pi_pop_up_main_container .pi_pop_up_form .pi_pop_up_terms_and_conditions_container .pi_pop_up_terms_and_conditions_text {
    position: absolute;
    background-color: #e8e8e8;
    padding: 5px;
    border: 2px solid grey;
    z-index: 1000;
    text-align: justify;
    bottom: 0px;
    left: 0px;
}

.pi_pop_up_main_container .pi_pop_up_form .pi_pop_up_terms_and_conditions_container a {
    color: #7b7b7b;
    font-family: Verdana, Geneva, sans-serif;
}

.pi_pop_up_first_name_control {
    width: calc(50% - 20px);
    float: left;
    border: 2px solid #2f4296;
    border-radius: 10px;
}

.pi_pop_up_last_name_control {
    width: calc(50% - 20px);
    float: right;
    border: 2px solid #2f4296;
    border-radius: 10px;
}

.pi_pop_up_phone_control {
    width: calc(50% - 20px);
