}

.pi_pop_up_submit_button {
    width: 97%;
    margin: 5px;
    border: 2px solid #2f4296;
    border-radius: 10px;
    color: white;
    font-size: 20px;
    line-height: 20px;
    padding: 8px;
    background-color: #2f4296;
}

.pi_pop_up_main_container input::-webkit-input-placeholder {
    color: inherit;
}

.pi_pop_up_main_container input:-moz-placeholder {
    color: inherit;
}

.pi_pop_up_main_container input::-moz-placeholder {
    color: inherit;
}

.pi_pop_up_main_container input:-ms-input-placeholder {
    color: inherit;
}

