var DynamicOffsets = {
    offsets: {
        top: {
            value: 100,
            desktop: {
                selectors: [".di-stacks--top", "#header"]
            },
            mobile: {
                selectors: [".di-stacks--top", ".menu-top"]
            }
        },
        bottom: {
            value: 100,
            desktop: {
                selectors: [".di-stacks--bottom"]
            },
            mobile: {
                selectors: [".di-stacks--bottom"]
            }
        }
    },
    getOffset: function(e) {
        return this.offsets[e] ? this.offsets[e].value : 100
    },
    setOffset: function(e, t) {
        this.offsets[e].value !== t && (this.offsets[e].value = t, this.dispatchEvent("DynamicOffsetChanged." + e, {
            value: t,
            offsets: this.offsets
        }))
    },
    createNewObserver: function(t) {
        return new ResizeObserver(function(e) {
            t(e)
        })
    },
    resizeObserver: function(t, s) {
        var o = this;
        return this.createNewObserver(function(e) {
            o.isDesktop() && "desktop" === s ? o.setOffset(t, e[0].contentRect.height) : o.isDesktop() || "mobile" !== s || o.setOffset(t, e[0].contentRect.height)
        })
    },
    isDesktop: function() {
        return 1024 < window.innerWidth
    },
    dispatchEvent: function(t, s) {
        try {
            var o = new CustomEvent(t, {
                detail: s
            })
        } catch (e) {
            (o = document.createEvent("CustomEvent")).initCustomEvent(t, !0, !0, s)
        }
        document.dispatchEvent(o)
    },
    getElementOf: function(e, t) {
        var s = this.offsets[e][t].selectors.join(",");
        return document.querySelector(s) || null
    },
    init: function() {
        var o = this;
        void 0 !== window.ResizeObserver ? window.addEventListener("DOMContentLoaded", function() {
            Object.keys(o.offsets).forEach(function(e) {
                var t = o.getElementOf(e, "desktop");
                t && o.resizeObserver(e, "desktop").observe(t);
                var s = o.getElementOf(e, "mobile");
                s && o.resizeObserver(e, "mobile").observe(s)
            })
        }) : console.log("window.ResizeObserver does not exist in this browser version.")
    }
};
DynamicOffsets.init();